import { Component } from 'react'
import tinytime from 'tinytime'
import styled from 'styled-components'
import Widget from '../../widget'
import ImageSrc from '../../../public/static/favicon.png'

const Wrap = styled.div`
  width: 100vw;
  height: 100vh;
  background-size: cover;
  background-position: center;
  background-repeat: no-repeat;
  background-image: ${(props) =>
    `url(${require(`../images/${props.backgroundImage}`).default})`};
  display: flex;
  flex-direction: column;
  justify-content: space-between; //vertical alignment because of flex direction column
  align-items: center; //horizontal alignment because of flex direction column
`;

const TimeItem = styled.div`
  font-size: 4em;
  text-align: center;
`

const DateItem = styled.div`
  font-size: 1.5em;
  text-align: center;
`

export default class List extends Component {
  static defaultProps = {
    interval: 1000 * 10
  }

  state = {
    date: new Date()
  }

  componentDidMount () {
    const { interval } = this.props

    this.interval = setInterval(() => this.updateTime(), interval)
  }

  updateTime () {
    this.setState({ date: new Date() })
  }

  componentWillUnmount () {
    clearInterval(this.interval)
  }

  render () {
    const { date } = this.state
    return (
      <Widget>
        <Wrap backgroundImage={backgroundImage}></Wrap>
        <TimeItem>{tinytime('{H}:{mm}:{ss}').render(date)}</TimeItem>
        <DateItem>{tinytime('{DD} {MM} {YYYY}').render(date)}</DateItem>
      </Widget>
    )
  }
}
