import { Component } from 'react'
import tinytime from 'tinytime'
import styled from 'styled-components'
import Widget from '../../widget'

const urlImage = require("../../../public/static/dog.png");

const MyIcon = styled.img`
  width: 100px;
  height: auto;
`;
MyIcon.defaultProps = {
  src: url
};

const MyIcon2 = styled.img.attrs(
  props => ({
    src: props.src || url
  })
)`
  width: 100px;
  height: auto;
`;


export default class List extends Component {
  static defaultProps = {
    interval: 1000 * 10
  }

  state = {
    date: new Date()
  }

  componentDidMount () {
    const { interval } = this.props

    this.interval = setInterval(() => this.updateTime(), interval)
  }

  updateTime () {
    this.setState({ date: new Date() })
  }

  componentWillUnmount () {
    clearInterval(this.interval)
  }

  render () {
    const { title } = this.props
    return (
      <Widget title={title}>
        <TimeItem>{tinytime('{H}:{mm}:{ss}').render(date)}</TimeItem>
        <DateItem>{tinytime('{DD} {MM} {YYYY}').render(date)}</DateItem>
      </Widget>
    )
  }
}
